import { Plugin } from "vite";
export interface TscPluginOption {
    tscCommand?: string;
}
/**
 * @example
 * import tscPlugin from "vite-plugin-tsc"
 *
 * export default {
 *   plugins: [tscPlugin()],
 *
 *   // Not required, but recommended.
 *   logLevel: "silent",
 * }
 */
export default function tscPlugin(option?: TscPluginOption): Plugin;
